/* === Define Custom Variables for Tab Styling === */
#root.root {
transition: all 300ms ease-in-out !important;}

#root.root::before {
  content: "";
  position: fixed;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  background-color: light-dark(white, dimgrey);
  pointer-events: none; /* Allows interaction with the content behind the overlay */
  z-index: 20; /* Places the overlay behind the content */
opacity:0;
}

@media (prefers-color-scheme: light) {
.ScrollBox > .top-shadow {
  top: -12px;
  left: 0;
  box-shadow: 0 1px 0 0 rgba(0,0,0,.051),0 1px 12px 0 rgba(0,0,0,.208);
}
.ScrollBox > .bottom-shadow {
  left: 0;
  bottom: -12px;
  box-shadow: 0-1px 0 0 rgba(0,0,0,.051),0-1px 12px 0 rgba(0,0,0,.208);
}
}

#root.root {

  --tabs-indent: 18px;
  --tabs-inner-gap: 6px;
  --tabs-pinned-height: 42px;
  --tabs-pinned-width: 42px;
  --general-margin: 8px;
  --tabs-height: 32px;
  --history-item-padding-left: 6px;
  --history-item-margin: 5px;
  --history-item-border-radius: 8px;
  --tabs-close-btn-margin: 3px;
  --nav-btn-margin: 0px 6px;
  --nav-btn-width: 32px;
  --nav-btn-height: 32px;
  --tabs-activated-shadow: inset 0 0 0 1px light-dark(rgba(0,0,0,0.8),rgba(255,255,255,0.3));
  --ntb-btn-height: 28px;
  --tabs-lvl-opacity: 0.4;

  transition: none; /* Disable transitions initially */
  min-width: 48px !important; /* Set minimum width */
}

.TabsPanel .bottom-space, .new-tab-btns {
background:transparent !important;
}
/* === Main Box Styling === */
#root.root .main-box {
  transition: opacity 200ms ease-in-out;
  padding:0;
}



/* === Tab colors ==== */
.scrollable .Tab .color-layer {
width: 32px !important;
}
.scrollable .Tab[data-active="true"] .color-layer {
border-radius: 4px 0px 0px 4px !important;
} 
.scrollable .Tab:hover .color-layer {
border-radius: 4px 0px 0px 4px !important;
}
.scrollable .Tab[data-colorized="true"] .t-box {
padding:0 6px !important;
}
.scrollable .t-box {
padding:0 3px;}


#root.root:not(:hover) .main-box {
  opacity: 1; /* Reduced opacity when not hovered */
}

#root.root #search_bar {
order:2;
padding-left:0px;
padding-right:6px;
}


.Tab .exp-icon {
opacity:1 !important;}

/* === Tab Panels Styling === */
.left-vertical-box #nav_bar{
background-color:transparent !important;
padding:12px 6px;
}



#root.root .top-horizontal-box {
  display: flex;
  order: 3 !important;
  margin: 0 auto !important;
}

.top-horizontal-box #nav_bar {
  background-color: transparent !important; /* Transparent background for nav bar */
}

#root.root:not(:hover) .top-horizontal-box {
  opacity: 1; /* Reduced opacity when not hovered */
}



/* ===                          === */
/* === Media Query for Autohide === */

@media (max-width: 90px) {
  #root.root {
    --tabs-pinned-height: 12px; /* Reduced height for pinned tabs */
    --tabs-pinned-width: 12px; /* Reduced width for pinned tabs */
    --tabs-indent: 0; /* No indentation for tabs */
    --tabs-lvl-opacity: 0; /* No opacity for tabs */
  }
.Tab .t-box {display:none !important;}
.PinnedTabsBar .color-layer {
display:none !important;
} 
#root.root .PinnedTabsBar,
#root.root .PinnedTabsBar .Tab {
  background-color: transparent !important;}
.PinnedTabsBar .Tab {opacity:0.7 !important;}
.PinnedTabsBar .Tab[data-pin="true"] {opacity:1 !mportant; }

.scrollable .Tab[data-colorized="true"] .color-layer {
opacity:0.1;
transition:opacity 200ms linear !important;
}


.left-vertical-box {display:none !important;}

#root.root #search_bar {
display:none;
}
  #root.root .new-tab-btns {
    display: none; /* Hide new tab buttons */
  }

  #root.root .top-horizontal-box .nav-item:not([data-class="panel"]),
  #root.root .nav-item[data-active="false"] {
    display: none; /* Hide specific nav items */
  }

  #root.root .Tab[data-pin="true"] {
    max-width: 32px !important;
    max-height: 32px !important; /* Set max width and height for pinned tabs */
  }
}



/* === Reposition Active Audio Indicator === */
#root.root:not(:hover) .Tab .audio {
  margin-left: -11px;
  margin-top: -7px;
}

#root.root:not(:hover) .Tab .audio-icon {
  height: 12px !important; /* Set height for audio icon when not hovered */
}

/* === Fix Padding Top and Bottom === */
#root.root .PinnedTabsBar,
#root.root .ScrollBox .scrollable,
#root.root .top-horizontal-box {
  padding-top: 8px !important; /* Set padding top for specific elements */
}

#root.root .top-horizontal-box {
  padding-bottom: 8px !important; /* Set padding bottom for top horizontal box */
}

/* === Pinned Tabs Styling === */
#root.root .PinnedTabsBar,
#root.root .PinnedTabsBar .Tab {
  background-color: light-dark(rgba(255, 255, 255, 0.4), rgba(0, 0, 0, 0.17));
  margin-top: 0px !important; /* Set background color and margin for pinned tabs */
border-bottom:1px solid white, 2px solid black;
}

/* === Tab Panel Box === */
#root.root .top-horizontal-box .main-items {
  justify-content: center;
  align-items: center;
}


/* === Random Styling === */
#root.root .Tab .child-count {
  opacity: 1 !important; /* Set opacity for child count */
}

#root.root .top-horizontal-box {
  padding-top: 15px;
  margin-bottom: -2px !important; /* Adjust padding and margin for top horizontal box */
}

#root.root .new-tab-btns {
  padding-top: 0px;
padding-bottom: 0px;
  margin-bottom: 0px !important; /* Adjust padding and margin for new tab buttons */
}


/* === Convert Space element in navigation panel to line break === */

#root.root .top-horizontal-box .nav-item[data-class="space"] {
  display: block !important;
  min-width: 100% !important;
  height: 2px !important; /* Set specific styling for space nav item */
}
